import React, {useState} from 'react'


export default function TextForm(props) {
  const handleUpClick = ()=>{
    // console.log("Uppercase was clicked" +  text);
    let newText = text.toUpperCase();
    setText(newText)
  }

  const handleLoClick = ()=>{
    // console.log("Uppercase was clicked" +  text);
    let newText = text.toLowerCase();
    setText(newText)
  }

  const handleClearClick = ()=>{
    // console.log("Uppercase was clicked" +  text);
    let newText = '';
    setText(newText)
  }


  const handleOnChange = (event)=>{
    // console.log("On Change");
    setText(event.target.value)
  }

  const handleCopy = ()=> {
    // console.log("I am copy");
    var text = document.getElementById("myBox");
    text.select();
    navigator.clipboard.writeText(text.value);
  }

  const handleExtraSpaces = (event)=>{
    // console.log("Extra Spaces");
    setText(event.target.value)
  }


  const [text, setText] = useState('');

  return (
    <>
    <div className="container" > 
            <h1>{props.heading}</h1>
          <div className="mb-3">
            <textarea className="form-control" value={text} onChange={handleOnChange} id="myBox" rows="8"></textarea>  
          </div>    
          <button className="btn btn-primary mx-2" onClick={handleUpClick}>Convert to Uppercse</button>
          <button className="btn btn-primary mx-2" onClick={handleLoClick}>Convert to Lowercase</button>
          <button className="btn btn-primary mx-2" onClick={handleClearClick}>Clear Text</button>
          <button className="btn btn-primary mx-2" onClick={handleCopy}>Copy Text</button>
          <button className="btn btn-primary mx-2" onClick={handleExtraSpaces}>Remove Spaces</button>



    </div>
    <div className="container my-3">
        <h1>Your text summury</h1>
        <p>{text.split(" ").length} words and {text.length} characters</p>
        <p>{0.008 * text.split(" ").length} Minutes read</p>
        <h2>Preview</h2>
        <p>{text}</p>

    </div>
    </>
  )
}
